export default class Title {
    /**
     * @default 1
     */
    alpha: number;
    /**
     * Specifies if the tile is bold or not.
     * @default false
     */
    bold: boolean;
    /**
     * Text color of a title.
     */
    color: string;
    /**
     * Unique id of a Title. You don't need to set it, unless you want to.
     */
    id: string;
    /**
     * Text size
     */
    size: number;
    /**
     * Text of a label
     */
    text: string;
}
