interface IPromiseLike<T = any> {
  then<TResult1 = T, TResult2 = never>(onfulfilled?: ((value: T) => TResult1 | IPromiseLike<TResult1>) | undefined | null, onrejected?: ((reason: any) => TResult2 | IPromiseLike<TResult2>) | undefined | null): IPromiseLike<TResult1 | TResult2>;
}

interface IPromise<T = any> {
  always<TResult1 = T>(callback?: ((valueOrError: T | any) => TResult1 | IPromiseLike<TResult1>) | undefined | null): IPromise<TResult1>;
  cancel?<U>(reason?: U, strict?: boolean): U;
  catch<TResult = never>(onrejected?: ((reason: any) => TResult | IPromiseLike<TResult>) | undefined | null): IPromise<T | TResult>;
  isCanceled?(): boolean;
  isFulfilled(): boolean;
  isRejected(): boolean;
  isResolved(): boolean;
  otherwise<TResult = never>(onrejected?: ((reason: any) => TResult | IPromiseLike<TResult>) | undefined | null): IPromise<T | TResult>;
  then<TResult1 = T, TResult2 = never>(onfulfilled?: ((value: T) => TResult1 | IPromiseLike<TResult1>) | undefined | null, onrejected?: ((reason: any) => TResult2 | IPromiseLike<TResult2>) | undefined | null): IPromise<TResult1 | TResult2>;
}

