/**
 * Type describing a syntax highlighting scheme as a JS object, as used in redux dev tools
 * Based on https://github.com/chriskempson/base16
 */
export interface ColorScheme {
  scheme: string
  author: string
  base00: string
  base01: string
  base02: string
  base03: string
  base04: string
  base05: string
  base06: string
  base07: string
  base08: string
  base09: string
  base0A: string
  base0B: string
  base0C: string
  base0D: string
  base0E: string
  base0F: string
}

