import { Emitter, EmitterMixinDelegateChain } from "@ckeditor/ckeditor5-utils/src/emittermixin";
import EventInfo from "@ckeditor/ckeditor5-utils/src/eventinfo";
import { BindChain, Observable } from "@ckeditor/ckeditor5-utils/src/observablemixin";
import Editor from "./editor/editor";
import { PriorityString } from "@ckeditor/ckeditor5-utils/src/priorities";
import { DomEventData } from "@ckeditor/ckeditor5-engine";

export default class Command implements Emitter, Observable {
    value?: unknown | undefined;
    readonly editor: Editor;
    isEnabled: boolean;

    constructor(editor: Editor);
    clearForceDisabled(id: string): void;
    destroy(): void;
    execute(...options: any[]): void;
    forceDisabled(id: string): void;
    refresh(): void;

    set(option: Record<string, unknown>): void;
    set(name: string, value: unknown): void;
    bind(...bindProperties: string[]): BindChain;
    unbind(...unbindProperties: string[]): void;
    decorate(methodName: string): void;

    on: (
        event: string,
        callback: (info: EventInfo, data: DomEventData) => void,
        options?: { priority: PriorityString | number },
    ) => void;
    once(
        event: string,
        callback: (info: EventInfo, data: DomEventData) => void,
        options?: { priority: PriorityString | number },
    ): void;
    off(event: string, callback?: (info: EventInfo, data: DomEventData) => void): void;
    listenTo(
        emitter: Emitter,
        event: string,
        callback: (info: EventInfo, data: DomEventData) => void,
        options?: { priority?: PriorityString | number | undefined },
    ): void;
    stopListening(emitter?: Emitter, event?: string, callback?: (info: EventInfo, data: DomEventData) => void): void;
    fire(eventOrInfo: string | EventInfo, ...args: any[]): any;
    delegate(...events: string[]): EmitterMixinDelegateChain;
    stopDelegating(event?: string, emitter?: Emitter): void;
}

// Beware that this defines a command constructor, not the class instance.
export interface CommandInterface<T = Command> {
    new (editor: Editor): T;
}
