/**
 * Check if the supplied parameter is undefined and throws and error
 * @param checkParam the parameter to validate for undefined
 * @param errorMsg the error message to be thrown
 */
export function validateParameterUndefined(checkParam: any, errorMsg: any): void;
/**
 * Check if the supplied parameter is undefined or null and throws and error
 * @param checkParam the parameter to validate for undefined
 * @param errorMsg the error message to be thrown
 */
export function validateParameterUndefinedOrNull(checkParam: any, errorMsg: any): void;
