declare namespace GorillaEngine.UI {
    interface Bounds {
        /**
         * The **x-coordinate** of the component's top-left corner, typically measured from the left edge of its containing element or viewport.
         */
        x: number;
        /**
         * The **y-coordinate** of the component's top-left corner, typically measured from the top edge of its containing element or viewport.
         */
        y: number;
        /**
         * The **width** of the component in pixels.
         */
        width: number;
        /**
         * The **height** of the component in pixels.
         */
        height: number;
    }
}
