    CLICK = 'click',
    DBLCLICK = 'dblclick',
    POINTERDRAG = 'pointerdrag',
    POINTERMOVE = 'pointermove',
    POINTERDOWN = 'pointerdown',
    POINTERUP = 'pointerup',
    POINTEROVER = 'pointerover',
    POINTEROUT = 'pointerout',
    POINTERENTER = 'pointerenter',
    POINTERLEAVE = 'pointerleave',
    POINTERCANCEL = 'pointercancel',
