/**
 * Docs: https://github.com/suryapratapsinghsuryavanshi/solverjs/blob/main/src/time/time.js
 */

/**
 * nanosecond to micorsecond.
 */
export function nsToUs(ns: number): number;

/**
 * nanosecond to milisecond.
 */
export function nsToMs(ns: number): number;

/**
 * nanosecond to second.
 */
export function nsToSc(ns: number): number;

/**
 * nanosecond to minute.
 */
export function nsToMi(ns: number): number;

/**
 * nanosecond to hour.
 */
export function nsToHr(ns: number): number;

/**
 * nanosecond to day.
 */
export function nsToDd(ns: number): number;

/**
 * nanosecond to week.
 */
export function nsToWk(ns: number): number;

/**
 * nanosecond to month.
 */
export function nsToMm(ns: number): number;

/**
 * nanosecond to year.
 */
export function nsToYy(ns: number): number;
