/**
 * Represents a sort-initiator.
 */
export type SortInitiator =
    /**
     * Indicates the user.
     */
    "user" |

    /**
     * Indicates the `sort`-event.
     */
    "sort" |

    /**
     * Indicates the `sorton`-method.
     */
    "sorton" |

    /**
     * Indicates the `sortAppend`-configuration.
     */
    "sortAppend" |

    /**
     * Indicates the `sortForce`-configuration.
     */
    "sortForce";
